      /**
       * Initialize the "Return to Grid" link
       */
      Video.initGridTrigger = function() {
        $('.grid-trigger').click(function(e) {
          e.preventDefault();

          $('body').removeClass('video-carousel-view');

          Video.effects.scrollToTop(function() {
            Video.effects.hideCarousel(function() {
              Video.effects.hidePlayer(function() {
                Video.effects.showGrid(function() {
                  Video.effects.highlightPlaying();
                  Video.effects.openForm();
                });
              });
            });
          });
        });
      };

      /**
       * Initialize the video trigger links.
       */
      Video.initVideoTriggers = function() {
        $('a.video-trigger').not('.video-trigger-processed').each(function() {

          $(this)
            .addClass('video-trigger-processed')
            .on('click', function(e) {
              e.preventDefault();
              Video.play($(this));
              $('a.video-trigger').removeClass('selected');
              $(this).addClass('selected');
            });
        });
      };

      /**
       * Play a video.
       * @param {jQuery} $trigger The clicked element wrapped in jQuery
       */
      Video.play = function($trigger) {
        var isCarouselView = $('body').hasClass('video-carousel-view'),
          videoID        = $trigger.data('videoid'),
          // HTML for the player in a related div with comment marks.
          // These divs live within each video tile wrapping div.
          playerHTML     = $('#'+videoID).html().replace('<!--', '').replace('-->', '');

        Video.currentVideoID = videoID;

        Video.effects.collapseForm();

        // video playback only happens in carousel view
        if ( ! isCarouselView) {
          // visual effects in a row
          Video.effects.hideGrid(function() {
            Video.effects.scrollToTop(function() {
              Video.effects.showPlayer(function() {
                $('#jukebox-window').html(playerHTML);
                Video.initCarousel(function() {
                  Video.effects.highlightPlaying();
                });
              });
            });
          });
        }
        else {
          $('#jukebox-window').html(playerHTML);
          Video.effects.scrollToTop();
          Video.effects.highlightPlaying();
        }
      };

      /**
       * Visual effects
       */
      Video.effects = {};

      /**
       * Carousel effects
       */
      Video.effects.hideCarousel = function(callback) {
        $('.container-media-carousel, .container-media-grid .carousel-nav')
          .slideUp('fast', function() {
            $(this).remove();
            if (callback) callback();
          });
      };

      /**
       * Filter form effects
       */
      Video.effects.collapseForm = function(callback) {
        $('.toggle-form')
          .find('.toggle-context').removeClass('visible').addClass('pre-collapsed')
            .find('.toggle-target').slideUp('fast');

        if (callback) callback();
      };
      Video.effects.openForm = function(callback) {
        $('.toggle-form')
          .find('.toggle-context').addClass('visible').removeClass('pre-collapsed')
            .find('.toggle-target').slideDown('fast');

        if (callback) callback();
      };

      /**
       * Grid effects
       */
      Video.effects.hideGrid = function(callback) {
        $('.container-media-results').slideUp(700, function() {
          $('.container-media').addClass('grid-off');
          $('.pager-load-more').hide();
          if (callback) callback();
        });
      };
      Video.effects.showGrid = function(callback) {
        $('.container-media-results').show(); // not seen yet
        $('.pager-load-more').show();
        rowUnify();
        $('.container-media').removeClass('grid-off'); // last step: .container-media-results is now visible
        if (callback) callback();
      };

      /**
       * Player effects
       */
      Video.effects.hidePlayer = function(callback) {
        $('#jukebox-window').empty();
        setTimeout(function() {
          $('#jukebox').slideUp(400, function() {
            if (callback) callback();
          });
        }, 200);

      };
      Video.effects.showPlayer = function(callback) {
        $('#jukebox').slideDown(400, callback);
      };

      /**
       * Highlight the currently playing video.
       */
      Video.effects.highlightPlaying = function(callback) {
        $('.container-media-carousel figure').removeClass('playing');
        $('.container-media-carousel figure').find('.play-video').empty();
        $('.container-media-carousel figure.fig-' + Video.currentVideoID).addClass('playing');
        $('.container-media-carousel figure.fig-' + Video.currentVideoID).find('.play-video').html('<strong>Now Playing</strong>');
        if (callback) callback();
      };

      /**
       * Scroll to the top to reveal the video
       */
      Video.effects.scrollToTop = function(callback) {

        if ($(window).innerHeight() < 600) {
          $('body,html').animate({scrollTop: 270}, 400, null, callback);
        } else {
          $('body,html').animate({scrollTop: 0}, 400, null, callback);
        }
      };

