      /*-------------------------------------------
        DONUT CHART
      -------------------------------------------*/

      function chartInit() {

        $('.chart-donut').each(function( index ) {
          var chartParentWidth  = $(this).parent().width(),
            // ensure the label has a percent sign if it's only numeric
            rawLabel = ($(this).attr('data-percent').match(/^[0-9]{1,3}$/)) ? $(this).attr('data-percent') + '%' : $(this).attr('data-percent'),
            // wrap '%' in a span for style
            chartPercentLabel = rawLabel.replace(/(%)/, '<span class="percent">$1</span>'),
            // numeric value only for math
            chartPercentValue = rawLabel.match(/[0-9]{1,3}/),
            chartSymbol = $(this).attr('data-symbol'),
            chartOptions = {
              segmentStrokeColor : "rgba(0,0,0,0)",
              segmentStrokeWidth : 0,
              percentageInnerCutout : 70,
              animation : false
            };

          if ( ! $('html').hasClass('canvas'))
          {
            // either no <canvas> support or it's not a percentage - no chart
            if (chartSymbol && $(this).siblings('span.label').length === 0) {
              $(this).parent().append('<span class="label"><span class="symbol">'+chartSymbol+'</span>'+chartPercentLabel+'</span>');
            } else if ($(this).siblings('span.label').length === 0) {
              $(this).parent().append('<span class="label">'+chartPercentLabel+'</span>');
            }
          }
          else
          {
            // has <canvas> support and it's a percentage - make chart
            $(this).attr({
              width: chartParentWidth,
              height: chartParentWidth
            });
            if (chartPercentValue == 100) {
              new Chart($(this).get(0).getContext("2d")).Doughnut([{value : 1*chartPercentValue, color : "#987414"}],chartOptions);
            } else if (chartPercentValue >= 97) {
              new Chart($(this).get(0).getContext("2d")).Doughnut([{value : 1*chartPercentValue, color : "#987414"},{value : 1.5, color : "rgba(255,255,255,0)"},{value : 100-chartPercentValue, color : "rgba(202,202,202,0.5)"}],chartOptions);
            }
            else {
              new Chart($(this).get(0).getContext("2d")).Doughnut([{value : 1*chartPercentValue, color : "#987414"},{value : 1.5, color : "rgba(255,255,255,0)"},{value : 103-chartPercentValue, color : "rgba(202,202,202,0.5)"},{value : 1.5, color : "rgba(255,255,255,0)"}],chartOptions);
            }
            if ($(this).siblings('div.label').length === 0) {
              $(this).after('<div class="label">'+chartPercentLabel+'</div>');
            }
            var chartHeight = $(this).height();
            if (chartSymbol && $(this).siblings('div.label').children('.symbol').length === 0) {
              $(this).parent().addClass('has-symbol');
              $(this).siblings('.label').css('line-height',''+chartHeight+'px').prepend('<span class="symbol">'+chartSymbol+'</span>');
            } else {
              $(this).siblings('.label').css('line-height',''+chartHeight+'px');
            }
          }
        });
      }

