      /*-------------------------------------------
         ROW: UNIFY & CROP
      -------------------------------------------*/

      function rowUnify() {
        $('.row-unify').each(function( index ) {
          var unifyHeight = -1,
          $rowContext = $(this),
          rowWidth = $(this).innerWidth(),
          articleWidth = $(this).find('article').width(),
          previousCells = $([]),
          lastTop = false,
          unifyHeight = 0;

          $rowContext.find('article').css('height','').each(function() {
            var top = $(this).offset().top;
            if (top === lastTop || lastTop === false) {
              previousCells = previousCells.add(this);
            }
            else {
              previousCells.height(unifyHeight);
              previousCells = $(this);
              top = $(this).offset().top;
              unifyHeight = 0;
            }
            unifyHeight = unifyHeight > $(this).height() ? unifyHeight : $(this).height();
            lastTop = top;
          });

          previousCells.height(unifyHeight);

          if ($rowContext.hasClass('row-crop')) {
            $('.row-crop article').removeClass('obscure');
            if ($(window).innerWidth() < 560) {
              $('.row-crop article').slice(-1).addClass('obscure');
            } else {
              var count = $('.row-crop article').length % 3;
              if (count == 0) count = 3;
              $('.row-crop article').slice(-count).addClass('obscure');
            }
          }
        });
      }

